import{r as n,o as c,c as s,a as e,b as i,w as d,F as l,d as o,e as a}from"./app.aed25a12.js";import{_ as r,a as p,b as u}from"./license.4fa21eaa.js";import{_ as h}from"./article-template.c2ec8bd3.js";import{_ as m}from"./plugin-vue_export-helper.21dcd24c.js";var b="/images/article-template-twocolumn.jpg";const f={},g=e("h1",{id:"article-template",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#article-template","aria-hidden":"true"},"#"),o(" Article Template")],-1),k=e("p",null,[e("a",{href:"https://www.overleaf.com/docs?snip_uri=https://github.com/dzwaneveld/TU-Delft-Unofficial-Article-Template/archive/master.zip",target:"_blank"},[e("img",{alt:"Open in Overleaf",src:r})]),e("a",{href:"https://github.com/dzwaneveld/TU-Delft-Unofficial-Article-Template/archive/master.zip"},[e("img",{alt:"Download .zip",src:p,hspace:"4"})]),e("a",{href:"https://creativecommons.org/licenses/by-nc/4.0/",target:"_blank"},[e("img",{alt:"License CC BY-NC 4.0",src:u})])],-1),y=a('<p>This template is a simple article template following all the guidelines of AE2333-I, based on the official AIAA template. Some of the main features:</p><ul><li><strong>Effortless:</strong> Many common packages are included by default to get started immediately;</li><li><strong>Switch Columns:</strong> Use one column, or switch to two columns at any time with minimal effort;</li><li><strong>Extensive Bibliography:</strong> A sample reference is included of the most common reference types to make sure all information is included.</li></ul><p>This template is created for <em>pdfLaTeX</em>, but it is also compatible with <em>XeLaTeX</em> or <em>LuaLaTeX</em>. <em>Natbib</em> is used for the bibliography with as backend <em>BibTeX</em>. If you would like to use it on Overleaf, click the button above to get started immediately.</p><p align="center"><img alt="Article Template" src="'+h+'" width="40%" style="border:1px solid #c7c7c7;"> \xA0 <img alt="Article Template Twocolumn" src="'+b+'" width="40%" style="border:1px solid #c7c7c7;"></p>',4),w={class:"custom-container tip"},_=e("p",{class:"custom-container-title"},"TIP",-1),v=o("In contrast to the report template, this template has been created over one semester for a single article. As such, there is likely more room for adjustments. Feel free to "),x=o("contact me"),T=o(" if you have suggestions or encounter issues!"),A=a(`<h2 id="document-structure" tabindex="-1"><a class="header-anchor" href="#document-structure" aria-hidden="true">#</a> Document Structure</h2><h3 id="structure" tabindex="-1"><a class="header-anchor" href="#structure" aria-hidden="true">#</a> Structure</h3><p>As an article can become quite a lengthy document, the main sections (such as the introduction and methodology) are separated into different files for convenience. All these files are inserted in the master file, <code>main.tex</code>, using the <code>\\input{filename}</code> command. The abstract, nomenclature, acknowledgment and appendix can be found in the master file instead. The document class, which can be found in <code>layout/tudelft-aiaa.cls</code>, is based on the article class by AIAA.</p><p>Finally, the bibliography is added using <code>\\bibliography{article.bib}</code>, which uses the file <code>article.bib</code>. If you would like to change the default bibliography file, change the command accordingly, which can be found in the master file. See Section 3 for more detail about the bibliography.</p><h3 id="two-columns-option" tabindex="-1"><a class="header-anchor" href="#two-columns-option" aria-hidden="true">#</a> Two Columns Option</h3><p>By default, the article is only one column. Switching to two columns can be done by using the <code>twocolumn</code> global option, resulting in the first line in <code>main.tex</code> as can be seen below.</p><div class="language-latex ext-latex line-numbers-mode"><pre class="language-latex"><code><span class="token function selector">\\documentclass</span><span class="token punctuation">[</span>twocolumn<span class="token punctuation">]</span><span class="token punctuation">{</span><span class="token keyword">layout/tudelft-aiaa</span><span class="token punctuation">}</span>
</code></pre><div class="line-numbers"><span class="line-number">1</span><br></div></div><p>Floats, such as figures and tables, will behave like &#39;normal&#39; in <code>twocolumn</code> mode and will therefore only occupy one column. In order to have a float span both columns (for example in combination with minipages or subfigures), an asterisk can be added to the environment. An example of a figure that will span both columns can be found below.</p><div class="language-latex ext-latex line-numbers-mode"><pre class="language-latex"><code><span class="token function selector">\\begin</span><span class="token punctuation">{</span><span class="token keyword">figure*</span><span class="token punctuation">}</span><span class="token punctuation">[</span>ht<span class="token punctuation">]</span>
    <span class="token function selector">\\centering</span>
    <span class="token function selector">\\includegraphics</span><span class="token punctuation">[</span>width=0.8<span class="token function selector">\\linewidth</span><span class="token punctuation">]</span><span class="token punctuation">{</span>image.jpg<span class="token punctuation">}</span>
    <span class="token function selector">\\caption</span><span class="token punctuation">{</span>Caption<span class="token punctuation">}</span>
    <span class="token function selector">\\label</span><span class="token punctuation">{</span><span class="token keyword">fig:label</span><span class="token punctuation">}</span>
<span class="token function selector">\\end</span><span class="token punctuation">{</span><span class="token keyword">figure*</span><span class="token punctuation">}</span>
</code></pre><div class="line-numbers"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br></div></div><h2 id="guidelines" tabindex="-1"><a class="header-anchor" href="#guidelines" aria-hidden="true">#</a> Guidelines</h2><p>The Microsoft Word template for AE2223-I is the guiding template and nearly everything is taken care of in this template. In order to follow all guidelines regarding the article layout, the following information should still be noted:</p><ul><li>Paragraphs that follow a blank line should not be indented. Use <code>\\noindent</code> to correct these indentations locally if needed. This works automatically after headings (sections, etc.)</li><li>When referencing a figure or equation in text, <code>\\autoref{}</code> will abbreviate to &#39;Fig.&#39; or &#39;Eq.&#39; always. Use <code>\\ref{}</code> at the beginning of sentences and write out &#39;Figure&#39; or &#39;Equation&#39; fully.</li></ul><h2 id="bibliography" tabindex="-1"><a class="header-anchor" href="#bibliography" aria-hidden="true">#</a> Bibliography</h2><p>In the following sections, the most common entry types and (required/optional) fields per entry type are provided. Private communication or personal websites may only be incorporated in the main text or appear as footnotes. The DOI (digital object identifier) should always be given if it is available.</p><p>The optional fields outside the brackets are <strong>almost always required</strong>. Please refer to <code>article.bib</code> for actual examples. Make sure the actual output corresponds with the guidelines.</p><h3 id="articles" tabindex="-1"><a class="header-anchor" href="#articles" aria-hidden="true">#</a> Articles</h3><ul><li><code>@article</code><br> Required fields: <code>author</code> <code>title</code> <code>year</code> <code>journal</code><br> Optional fields: <code>volume</code> <code>number</code> <code>pages</code> <code>doi</code> (<code>url</code> <code>note</code>)</li></ul><h3 id="books" tabindex="-1"><a class="header-anchor" href="#books" aria-hidden="true">#</a> Books</h3><ul><li><p><code>@inbook</code><br> Required fields: <code>author</code>/<code>editor</code> <code>title</code> <code>year</code> <code>publisher</code><br> Optional fields: <code>address</code> <code>pages</code>/<code>chapter</code> (<code>series</code> <code>doi</code> <code>url</code> <code>note</code>)</p></li><li><p><code>@book</code><br> Required fields: <code>author</code>/<code>editor</code> <code>title</code> <code>year</code> <code>publisher</code><br> Optional fields: <code>address</code> (<code>series</code> <code>doi</code> <code>url</code> <code>note</code>)</p></li></ul><h3 id="proceedings" tabindex="-1"><a class="header-anchor" href="#proceedings" aria-hidden="true">#</a> Proceedings</h3><ul><li><p><code>@inproceedings</code><br> Required fields: <code>author</code> <code>title</code> <code>year</code> <code>booktitle</code> <code>pages</code><br> Optional fields: <code>publisher</code>/<code>organization</code> <code>address</code> (<code>volume</code> <code>series</code> <code>doi</code> <code>url</code> <code>note</code>)</p></li><li><p><code>@proceedings</code><br> Required fields: <code>title</code> <code>year</code><br> Optional fields: <code>editor</code> <code>publisher</code>/<code>organization</code> <code>address</code> (<code>volume</code> <code>number</code> <code>series</code> <code>doi</code> <code>url</code> <code>note</code>)</p></li></ul><h3 id="reports-theses-and-individual-papers" tabindex="-1"><a class="header-anchor" href="#reports-theses-and-individual-papers" aria-hidden="true">#</a> Reports, Theses, and Individual Papers</h3><ul><li><p><code>@report</code><br> Required fields: <code>author</code> <code>title</code> <code>year</code><br> Optional fields: <code>number</code> <code>institution</code> <code>address</code> (<code>doi</code> <code>url</code> <code>note</code>)</p></li><li><p><code>@mastersthesis</code><br> Required fields: <code>author</code> <code>title</code> <code>year</code> <code>school</code><br> Optional fields: <code>address</code> (<code>doi</code> <code>url</code> <code>note</code>)</p></li><li><p><code>@phdthesis</code><br> Required fields: <code>author</code> <code>title</code> <code>year</code> <code>school</code><br> Optional fields: <code>address</code> (<code>doi</code> <code>url</code> <code>note</code>)</p></li></ul>`,23);function q(I,O){const t=n("RouterLink");return c(),s(l,null,[g,k,y,e("div",w,[_,e("p",null,[v,i(t,{to:"/about.html#contact"},{default:d(()=>[x]),_:1}),T])]),A],64)}var z=m(f,[["render",q]]);export{z as default};
